import { useCallback, useEffect, useState } from "react";
import { Grid, Paper } from "@mui/material";
import "./MatrixContainer.css";
import Cell from "../Cell/Cell.tsx";
import { RobotContext } from "../../globalStates/RobotContext.js";
import { DirectionProps } from "../../types/DirectionProps.ts";

enum Direction {
  NORTH = "NORTH",
  SOUTH = "SOUTH",
  EAST = "EAST",
  WEST = "WEST",
}

interface Location {
  row: number | null;
  column: number | null;
}

const MatrixContainer = () => {
  const initRobot: Location = {
    row: null,
    column: null,
  };
  const [robotLocation, setRobotLocation] = useState(initRobot);
  const [robotDirection, setRobotDirection] = useState(DirectionProps.NORTH);

  const handleKeyDown = useCallback(
    (
      event: KeyboardEvent,
      robotDirection: DirectionProps,
      location: Location
    ) => {
      switch (robotDirection) {
        case "NORTH":
          if (event.key === "ArrowLeft") {
            setRobotDirection(DirectionProps.WEST);
          }
          if (event.key === "ArrowRight") {
            setRobotDirection(DirectionProps.EAST);
          }
          break;
        case "SOUTH":
          if (event.key === "ArrowLeft") {
            setRobotDirection(DirectionProps.EAST);
          }
          if (event.key === "ArrowRight") {
            setRobotDirection(DirectionProps.WEST);
          }
          break;
        case "EAST":
          if (event.key === "ArrowLeft") {
            setRobotDirection(DirectionProps.NORTH);
          }
          if (event.key === "ArrowRight") {
            setRobotDirection(DirectionProps.SOUTH);
          }
          break;
        case "WEST":
          if (event.key === "ArrowLeft") {
            setRobotDirection(DirectionProps.SOUTH);
          }
          if (event.key === "ArrowRight") {
            setRobotDirection(DirectionProps.NORTH);
          }
          break;
      }
      switch (event.key) {
        case "n":
          setRobotDirection(DirectionProps.NORTH);
          break;
        case "s":
          setRobotDirection(DirectionProps.SOUTH);
          break;
        case "e":
          setRobotDirection(DirectionProps.EAST);
          break;
        case "w":
          setRobotDirection(DirectionProps.WEST);
          break;
        case "ArrowUp":
          handleMove(location, robotDirection);
      }
    },
    []
  );

  useEffect(() => {
    const keyDownHandler = (event: KeyboardEvent) =>
      handleKeyDown(event, robotDirection, robotLocation);
    document.addEventListener("keydown", keyDownHandler);

    return () => {
      document.removeEventListener("keydown", keyDownHandler);
    };
  }, [robotDirection, robotLocation, handleKeyDown]);

  const handleCellClick = (x: number, y: number) => {
    setRobotLocation({
      row: x,
      column: y,
    });
  };

  const handleMove = (location: Location, direction: DirectionProps) => {
    switch (direction) {
      case "NORTH":
        if (location.row === 4) {
          console.log("you can't move here!");
          break;
        }
        if (location.row !== null && location.column !== null) {
          setRobotLocation({ row: location.row + 1, column: location.column });
        }
        break;
      case "SOUTH":
        if (location.row === 0) {
          console.log("you can't move here!");
          break;
        }
        if (location.row !== null && location.column !== null) {
          setRobotLocation({ row: location.row - 1, column: location.column });
        }
        break;
      case "EAST":
        if (location.column === 4) {
          console.log("you can't move here!");
          break;
        }
        if (location.row !== null && location.column !== null) {
          setRobotLocation({ row: location.row, column: location.column + 1 });
        }
        break;
      case "WEST":
        if (location.column === 0) {
          console.log("you can't move here!");
          break;
        }
        if (location.row !== null && location.column !== null) {
          setRobotLocation({ row: location.row, column: location.column - 1 });
        }
        break;
    }
  };

  const getDirection = (direction: DirectionProps) => {
    switch (direction) {
      case "NORTH":
        return "rotate(0deg)";
      case "EAST":
        return "rotate(90deg)";
      case "SOUTH":
        return "rotate(180deg)";
      case "WEST":
        return "rotate(270deg)";
      default:
        return "rotate(0deg)";
    }
  };

  const renderCell = (row: number, column: number) => {
    const isRobotHere =
      robotLocation.row === row && robotLocation.column === column;

    if (isRobotHere) {
      console.log("row: ", row, ", column: ", column);
    }
    return (
      <Grid
        item
        xs={12 / 5}
        key={`${row}, ${column}`}
        style={{ display: "flex" }}
      >
        <Paper
          style={{
            height: "70px",
            flexGrow: 1,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
          onClick={() => handleCellClick(row, column)}
        >
          {isRobotHere && (
            <p
              style={{
                fontSize: "30px",
                width: "fit-content",
                transform: getDirection(robotDirection),
              }}
            >
              ðŸ¤–
            </p>
          )}
        </Paper>
      </Grid>
    );
  };

  const grid = [];
  for (let row = 4; row >= 0; row--) {
    const columns = [];
    for (let column = 0; column < 5; column++) {
      columns.push(
        <Cell
          key={column}
          row={row}
          column={column}
          isRobotHere={false}
          onClick={() => handleCellClick(row, column)}
          getDirection={getDirection(robotDirection)}
        />
      );
    }
    grid.push(
      <Grid container item xs={12} key={row}>
        {columns}
      </Grid>
    );
  }

  return (
    <RobotContext.Provider
      value={{
        robotLocation,
        setRobotLocation,
        robotDirection,
        setRobotDirection,
      }}
    >
      <div className="matrix--container">
        <Grid container spacing={1}>
          {grid}
        </Grid>
      </div>
    </RobotContext.Provider>
  );
};

export default MatrixContainer;
