import { useCallback, useEffect, useState } from "react";
import { Button, ButtonGroup, Grid, Paper } from "@mui/material";
import "./MatrixContainer.css";
import Cell from "../Cell/Cell.tsx";
import { RobotContext } from "../../globalStates/RobotContext.ts";
import { DirectionProps } from "../../types/DirectionProps.ts";
import { LocationProps } from "../../types/LocationProps.ts";

const MatrixContainer = () => {
  const initRobot: LocationProps = {
    row: null,
    column: null,
  };
  const [robotLocation, setRobotLocation] = useState(initRobot);
  const [robotDirection, setRobotDirection] = useState(DirectionProps.NORTH);
  const [message, setMessage] = useState("");

  const handleRotateLeft = useCallback((direction: DirectionProps) => {
    switch (direction) {
      case "NORTH":
        setRobotDirection(DirectionProps.WEST);
        break;
      case "EAST":
        setRobotDirection(DirectionProps.NORTH);
        break;
      case "SOUTH":
        setRobotDirection(DirectionProps.EAST);
        break;
      case "WEST":
        setRobotDirection(DirectionProps.SOUTH);
        break;
      default:
        setRobotDirection(DirectionProps.WEST);
        break;
    }
  }, []);

  const handleRotateRight = useCallback((direction: DirectionProps) => {
    switch (direction) {
      case "NORTH":
        setRobotDirection(DirectionProps.EAST);
        break;
      case "EAST":
        setRobotDirection(DirectionProps.SOUTH);
        break;
      case "SOUTH":
        setRobotDirection(DirectionProps.WEST);
        break;
      case "WEST":
        setRobotDirection(DirectionProps.NORTH);
        break;
      default:
        setRobotDirection(DirectionProps.EAST);
        break;
    }
  }, []);

  const handleResetDirection = (direction: string) => {
    switch (direction) {
      case "n":
        setRobotDirection(DirectionProps.NORTH);
        break;
      case "s":
        setRobotDirection(DirectionProps.SOUTH);
        break;
      case "e":
        setRobotDirection(DirectionProps.EAST);
        break;
      case "w":
        setRobotDirection(DirectionProps.WEST);
        break;
    }
  };

  const handleReport = (location: LocationProps, direction: DirectionProps) => {
    if (location.row !== null && location.column !== null) {
      setMessage(
        `Your robot is located at (${location.row}, ${location.column}) and is facing ${direction}`
      );
    }
  };

  const handleMoveForwards = (
    location: LocationProps,
    direction: DirectionProps
  ) => {
    switch (direction) {
      case "NORTH":
        if (location.row === 4) {
          setMessage("You can't move here!");
          break;
        }
        if (location.row !== null && location.column !== null) {
          setRobotLocation({ row: location.row + 1, column: location.column });
        }
        break;
      case "SOUTH":
        if (location.row === 0) {
          setMessage("You can't move here!");
          break;
        }
        if (location.row !== null && location.column !== null) {
          setRobotLocation({ row: location.row - 1, column: location.column });
        }
        break;
      case "EAST":
        if (location.column === 4) {
          setMessage("You can't move here!");
          break;
        }
        if (location.row !== null && location.column !== null) {
          setRobotLocation({ row: location.row, column: location.column + 1 });
        }
        break;
      case "WEST":
        if (location.column === 0) {
          setMessage("You can't move here!");
          break;
        }
        if (location.row !== null && location.column !== null) {
          setRobotLocation({ row: location.row, column: location.column - 1 });
        }
        break;
    }
  };

  const handleMoveBackwards = (
    location: LocationProps,
    direction: DirectionProps
  ) => {
    switch (direction) {
      case "NORTH":
        if (location.row === 0) {
          setMessage("You can't move here!");
          break;
        }
        if (location.row !== null && location.column !== null) {
          setRobotLocation({ row: location.row - 1, column: location.column });
        }
        break;
      case "SOUTH":
        if (location.row === 4) {
          setMessage("You can't move here!");
          break;
        }
        if (location.row !== null && location.column !== null) {
          setRobotLocation({ row: location.row + 1, column: location.column });
        }
        break;
      case "EAST":
        if (location.column === 0) {
          setMessage("You can't move here!");
          break;
        }
        if (location.row !== null && location.column !== null) {
          setRobotLocation({ row: location.row, column: location.column - 1 });
        }
        break;
      case "WEST":
        if (location.column === 4) {
          setMessage("You can't move here!");
          break;
        }
        if (location.row !== null && location.column !== null) {
          setRobotLocation({ row: location.row, column: location.column + 1 });
        }
        break;
    }
  };

  useEffect(() => {
    const keyDownHandler = (event: KeyboardEvent) => {
      setMessage("");
      if (event.key === "ArrowLeft") {
        handleRotateLeft(robotDirection);
      }
      if (event.key === "ArrowRight") {
        handleRotateRight(robotDirection);
      }
      const resetDirection = ["n", "s", "e", "w"];
      if (resetDirection.includes(event.key)) {
        handleResetDirection(event.key);
      }
      if (event.key === "ArrowUp") {
        handleMoveForwards(robotLocation, robotDirection);
      }
      if (event.key === "ArrowDown") {
        handleMoveBackwards(robotLocation, robotDirection);
      }
    };
    document.addEventListener("keydown", keyDownHandler);

    return () => {
      setMessage("");
      document.removeEventListener("keydown", keyDownHandler);
    };
  }, [robotDirection, robotLocation, handleRotateLeft, handleRotateRight]);

  const grid = [];
  for (let row = 4; row >= 0; row--) {
    const columns = [];
    for (let column = 0; column < 5; column++) {
      columns.push(<Cell key={column} row={row} column={column} />);
    }
    grid.push(
      <Grid container item xs={12} key={row}>
        {columns}
      </Grid>
    );
  }

  return (
    <RobotContext.Provider
      value={{
        robotLocation,
        setRobotLocation,
        robotDirection,
        setRobotDirection,
      }}
    >
      <div className="full-board--container">
        <ButtonGroup sx={{ mb: 2 }}>
          <Button
            onClick={() => handleMoveForwards(robotLocation, robotDirection)}
          >
            Move Forwards
          </Button>
          <Button
            onClick={() => handleMoveBackwards(robotLocation, robotDirection)}
          >
            Move Backwards
          </Button>
          <Button
            onClick={() => {
              handleRotateLeft(robotDirection);
            }}
          >
            Rotate Left
          </Button>
          <Button
            onClick={() => {
              handleRotateRight(robotDirection);
            }}
          >
            Rotate Right
          </Button>
          <Button
            onClick={() => handleReport(robotLocation, robotDirection)}
            onBlur={() => {
              setMessage("");
            }}
          >
            Report
          </Button>
        </ButtonGroup>
        <p style={{ height: "20px" }}>{message}</p>
        <div className="matrix--container">
          <Grid container spacing={0} border={"1px solid black"}>
            {grid}
          </Grid>
        </div>
      </div>
    </RobotContext.Provider>
  );
};

export default MatrixContainer;
