import { useState } from "react";
import { Grid, Paper } from "@mui/material";
import "./MatrixContainer.css";

interface Robot {
  row: number | null;
  column: number | null;
  direction: "north" | "south" | "east" | "west";
}

const MatrixContainer = () => {
  const initRobot: Robot = {
    row: null,
    column: null,
    direction: "north",
  };
  const [robot, setRobot] = useState({ x: null, y: null, facing: "NORTH" });

  const handleCellClick = (x: number, y: number) => {
    setRobot({ x, y, facing: "NORTH" });
  };

  const renderCell = (x: number, y: number) => {
    const isRobotHere = robot.x === x && robot.y === y;
    return (
      <Grid item xs={12 / 5} key={`${x}-${y}`}>
        <Paper onClick={() => handleCellClick(x, y)}>
          {isRobotHere ? "ðŸ¤–" : `${x},${y}`}
        </Paper>
      </Grid>
    );
  };

  return (
    <div className="matrix--container">
      <Grid container spacing={1}>
        {Array.from({ length: 5 }, (_, row) => 4 - row).map((row) =>
          Array.from({ length: 5 }).map((_, col) => renderCell(col, row))
        )}
      </Grid>
    </div>
  );
};

export default MatrixContainer;
