import { useEffect, useState } from "react";
import { Grid, Paper } from "@mui/material";
import "./MatrixContainer.css";

enum Direction {
  NORTH = "NORTH",
  SOUTH = "SOUTH",
  EAST = "EAST",
  WEST = "WEST",
}

interface Location {
  row: number | null;
  column: number | null;
  // direction: Direction;
}

const MatrixContainer = () => {
  const initRobot: Location = {
    row: null,
    column: null,
    // setting the initial direction to be north
    // direction: Direction.NORTH,
  };
  const [robotLocation, setRobotLocation] = useState(initRobot);
  const [robotDirection, setRobotDirection] = useState(Direction.NORTH);

  useEffect(() => {
    const keyDownHandler = (event: KeyboardEvent) =>
      handleKeyDown(event, robotDirection, robotLocation);
    document.addEventListener("keydown", keyDownHandler);

    return () => {
      document.removeEventListener("keydown", keyDownHandler);
    };
  }, [robotDirection]);

  const handleCellClick = (x: number, y: number) => {
    setRobotLocation({
      row: x,
      column: y,
    });
  };

  const handleMove = (location: Location, direction: Direction) => {
    switch (direction) {
      case "NORTH":
        if (location.row === 0) {
          console.log("you can't move here!");
        }
        if (location.row && location.column) {
          setRobotLocation({ row: location.row - 1, column: location.column });
        }
        break;
      case "SOUTH":
        break;
      case "EAST":
        break;
      case "WEST":
        break;
    }
  };

  const handleKeyDown = (
    event: KeyboardEvent,
    robotDirection: Direction,
    location: Location
  ) => {
    console.log("key down");
    switch (robotDirection) {
      case "NORTH":
        if (event.key === "ArrowLeft") {
          setRobotDirection(Direction.WEST);
        }
        if (event.key === "ArrowRight") {
          setRobotDirection(Direction.EAST);
        }
        break;
      case "SOUTH":
        if (event.key === "ArrowLeft") {
          setRobotDirection(Direction.EAST);
        }
        if (event.key === "ArrowRight") {
          setRobotDirection(Direction.WEST);
        }
        break;
      case "EAST":
        if (event.key === "ArrowLeft") {
          setRobotDirection(Direction.NORTH);
        }
        if (event.key === "ArrowRight") {
          setRobotDirection(Direction.SOUTH);
        }
        break;
      case "WEST":
        if (event.key === "ArrowLeft") {
          setRobotDirection(Direction.SOUTH);
        }
        if (event.key === "ArrowRight") {
          setRobotDirection(Direction.NORTH);
        }
        break;
    }
    switch (event.key) {
      case "n":
        setRobotDirection(Direction.NORTH);
        break;
      case "s":
        setRobotDirection(Direction.SOUTH);
        break;
      case "e":
        setRobotDirection(Direction.EAST);
        break;
      case "w":
        setRobotDirection(Direction.WEST);
        break;
      case "ArrowUp":
        handleMove(location, robotDirection);
    }
  };

  const getDirection = (direction: Direction) => {
    switch (direction) {
      case "NORTH":
        return "rotate(0deg)";
      case "EAST":
        return "rotate(90deg)";
      case "SOUTH":
        return "rotate(180deg)";
      case "WEST":
        return "rotate(270deg)";
    }
  };

  const renderCell = (row: number, column: number) => {
    const isRobotHere =
      robotLocation.row === row && robotLocation.column === column;
    return (
      <Grid
        item
        xs={12 / 5}
        key={`${row}, ${column}`}
        style={{ display: "flex" }}
      >
        <Paper
          style={{
            height: "70px",
            flexGrow: 1,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
          onClick={() => handleCellClick(row, column)}
        >
          {isRobotHere && (
            <p
              style={{
                fontSize: "30px",
                width: "fit-content",
                transform: getDirection(robotDirection),
              }}
            >
              🤖
            </p>
          )}
        </Paper>
      </Grid>
    );
  };

  const grid = [];
  for (let row = 4; row >= 0; row--) {
    const cols = [];
    for (let col = 0; col < 5; col++) {
      cols.push(renderCell(col, row));
    }
    grid.push(
      <Grid container item xs={12} key={row}>
        {cols}
      </Grid>
    );
  }

  return (
    <div className="matrix--container">
      <Grid container spacing={1}>
        {grid}
      </Grid>
    </div>
  );
};

export default MatrixContainer;
