import { useCallback, useEffect, useState } from "react";
import { Button, ButtonGroup, Grid, Paper } from "@mui/material";
import "./MatrixContainer.css";
import Cell from "../Cell/Cell.tsx";
import { RobotContext } from "../../globalStates/RobotContext.ts";
import { DirectionProps } from "../../types/DirectionProps.ts";

interface Location {
  row: number | null;
  column: number | null;
}

const MatrixContainer = () => {
  const initRobot: Location = {
    row: null,
    column: null,
  };
  const [robotLocation, setRobotLocation] = useState(initRobot);
  const [robotDirection, setRobotDirection] = useState(DirectionProps.NORTH);

  const handleRotateLeft = useCallback((robotDirection: DirectionProps) => {
    switch (robotDirection) {
      case "NORTH":
        setRobotDirection(DirectionProps.WEST);
        break;
      case "EAST":
        setRobotDirection(DirectionProps.NORTH);
        break;
      case "SOUTH":
        setRobotDirection(DirectionProps.EAST);
        break;
      case "WEST":
        setRobotDirection(DirectionProps.SOUTH);
        break;
      default:
        setRobotDirection(DirectionProps.WEST);
        break;
    }
  }, []);

  const handleRotateRight = useCallback((robotDirection: DirectionProps) => {
    switch (robotDirection) {
      case "NORTH":
        setRobotDirection(DirectionProps.EAST);
        break;
      case "EAST":
        setRobotDirection(DirectionProps.SOUTH);
        break;
      case "SOUTH":
        setRobotDirection(DirectionProps.WEST);
        break;
      case "WEST":
        setRobotDirection(DirectionProps.NORTH);
        break;
      default:
        setRobotDirection(DirectionProps.EAST);
        break;
    }
  }, []);

  // const handleRotate = useCallback(
  //   (
  //     event: KeyboardEvent,
  //     robotDirection: DirectionProps,
  //     location: Location
  //   ) => {
  //     switch (robotDirection) {
  //       case "NORTH":
  //         if (event.key === "ArrowLeft") {
  //           setRobotDirection(DirectionProps.WEST);
  //         }
  //         if (event.key === "ArrowRight") {
  //           setRobotDirection(DirectionProps.EAST);
  //         }
  //         break;
  //       case "SOUTH":
  //         if (event.key === "ArrowLeft") {
  //           setRobotDirection(DirectionProps.EAST);
  //         }
  //         if (event.key === "ArrowRight") {
  //           setRobotDirection(DirectionProps.WEST);
  //         }
  //         break;
  //       case "EAST":
  //         if (event.key === "ArrowLeft") {
  //           setRobotDirection(DirectionProps.NORTH);
  //         }
  //         if (event.key === "ArrowRight") {
  //           setRobotDirection(DirectionProps.SOUTH);
  //         }
  //         break;
  //       case "WEST":
  //         if (event.key === "ArrowLeft") {
  //           setRobotDirection(DirectionProps.SOUTH);
  //         }
  //         if (event.key === "ArrowRight") {
  //           setRobotDirection(DirectionProps.NORTH);
  //         }
  //         break;
  //     }
  //   },
  //   []
  // );

  const handleResetDirection = (direction: string) => {
    switch (direction) {
      case "n":
        setRobotDirection(DirectionProps.NORTH);
        break;
      case "s":
        setRobotDirection(DirectionProps.SOUTH);
        break;
      case "e":
        setRobotDirection(DirectionProps.EAST);
        break;
      case "w":
        setRobotDirection(DirectionProps.WEST);
        break;
    }
  };

  useEffect(() => {
    const keyDownHandler = (event: KeyboardEvent) => {
      if (event.key === "ArrowLeft") {
        handleRotateLeft(robotDirection);
      }
      if (event.key === "ArrowRight") {
        handleRotateRight(robotDirection);
      }
      const resetDirection = ["n", "s", "e", "w"];
      if (resetDirection.includes(event.key)) {
        handleResetDirection(event.key);
      }
      if (event.key === "ArrowUp" || event.key === "Enter") {
        handleMove(robotLocation, robotDirection);
      }
    };
    document.addEventListener("keydown", keyDownHandler);

    return () => {
      document.removeEventListener("keydown", keyDownHandler);
    };
  }, [robotDirection, robotLocation, handleRotateLeft, handleRotateRight]);

  const handleMove = (location: Location, direction: DirectionProps) => {
    switch (direction) {
      case "NORTH":
        if (location.row === 4) {
          console.log("you can't move here!");
          break;
        }
        if (location.row !== null && location.column !== null) {
          setRobotLocation({ row: location.row + 1, column: location.column });
        }
        break;
      case "SOUTH":
        if (location.row === 0) {
          console.log("you can't move here!");
          break;
        }
        if (location.row !== null && location.column !== null) {
          setRobotLocation({ row: location.row - 1, column: location.column });
        }
        break;
      case "EAST":
        if (location.column === 4) {
          console.log("you can't move here!");
          break;
        }
        if (location.row !== null && location.column !== null) {
          setRobotLocation({ row: location.row, column: location.column + 1 });
        }
        break;
      case "WEST":
        if (location.column === 0) {
          console.log("you can't move here!");
          break;
        }
        if (location.row !== null && location.column !== null) {
          setRobotLocation({ row: location.row, column: location.column - 1 });
        }
        break;
    }
  };

  const grid = [];
  for (let row = 4; row >= 0; row--) {
    const columns = [];
    for (let column = 0; column < 5; column++) {
      columns.push(<Cell key={column} row={row} column={column} />);
    }
    grid.push(
      <Grid container item xs={12} key={row}>
        {columns}
      </Grid>
    );
  }

  return (
    <RobotContext.Provider
      value={{
        robotLocation,
        setRobotLocation,
        robotDirection,
        setRobotDirection,
      }}
    >
      <div className="matrix--container">
        <ButtonGroup sx={{ mb: 2 }}>
          <Button onClick={() => handleMove(robotLocation, robotDirection)}>
            Move
          </Button>
          <Button>Rotate Left</Button>
          <Button>Rotate Right</Button>
          <Button>Report</Button>
        </ButtonGroup>
        <Grid container spacing={0} border={"1px solid black"}>
          {grid}
        </Grid>
      </div>
    </RobotContext.Provider>
  );
};

export default MatrixContainer;
