import { Grid, Paper } from "@mui/material";
import { CellProps } from "../../types/CellProps";
import { RobotContext } from "../../globalStates/RobotContext.ts";

const Cell = ({ row, column, onClick, getDirection }: CellProps) => {
  const context = useContext(RobotContext);
  const location = context?.robotLocation;
  const isRobotHere = location?.row === row && location?.column === column;

  return (
    <Grid
      item
      xs={12 / 5}
      key={`${row}, ${column}`}
      style={{ display: "flex" }}
    >
      <Paper
        style={{
          height: "70px",
          flexGrow: 1,
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
        onClick={() => onClick()}
      >
        {isRobotHere && (
          <p
            style={{
