import { useEffect, useState } from "react";
import { Grid, Paper } from "@mui/material";
import "./MatrixContainer.css";

enum Direction {
  NORTH,
  SOUTH,
  EAST,
  WEST,
}

interface Robot {
  row: number | null;
  column: number | null;
  direction: Direction;
}

const MatrixContainer = () => {
  const initRobot: Robot = {
    row: null,
    column: null,
    // setting the initial direction to be north
    direction: Direction.NORTH,
  };
  const [robot, setRobot] = useState(initRobot);

  useEffect(() => {
    document.addEventListener("keydown", (event) =>
      handleKeyDown(event, robot)
    );
  });

  const handleCellClick = (x: number, y: number) => {
    setRobot({
      row: x,
      column: y,
      direction: robot.direction,
    });
  };

  const handleKeyDown = (event: KeyboardEvent, robot: Robot) => {
    console.log("handle jey down");
    console.log(robot.direction);
    const newRobot = robot;
    switch (robot.direction) {
      case Direction.NORTH:
        if (event.key === "ArrowLeft") {
          console.log("arrow left");
          newRobot.direction = Direction.WEST;
        }
        if (event.key === "ArrowRight") {
          newRobot.direction = Direction.EAST;
        }
        break;
      case Direction.SOUTH:
        break;
      case Direction.EAST:
        break;
      case Direction.WEST:
        break;
    }
    setRobot(newRobot);
  };

  const renderCell = (row: number, column: number) => {
    const isRobotHere = robot.row === row && robot.column === column;
    const direction = robot.direction;
    return (
      <Grid item xs={12 / 5} key={`${row}, ${column}`}>
        <Paper onClick={() => handleCellClick(row, column)}>
          {isRobotHere ? "ðŸ¤–" : `${row},${column},${direction}`}
        </Paper>
      </Grid>
    );
  };

  const grid = [];
  for (let row = 4; row >= 0; row--) {
    const cols = [];
    for (let col = 0; col < 5; col++) {
      cols.push(renderCell(col, row));
    }
    grid.push(
      <Grid container item xs={12} key={row}>
        {cols}
      </Grid>
    );
  }

  return (
    <div className="matrix--container">
      <Grid container spacing={1}>
        {grid}
      </Grid>
    </div>
  );
};

export default MatrixContainer;
